import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './contenthistory.css';

const ContentHistoryCreate = () => {
    return (
        <div className="form-container">
            {/* <h2>{id ? 'Update Blog Post' : 'Upload Blog Post'}</h2> */}
            <h2>Upload Blog Post</h2>
            <form>
                <div className="form-group">
                    <label>Title</label>
                    <input
                        type="text"
                        name="title"
                        value=""
                        // onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Sub Title</label>
                    <input
                        type="text"
                        name="sub-title"
                        value=""
                        // onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Details</label>
                    <textarea
                        name="details"
                        value=""
                        // onChange={handleInputChange}
                        rows="10"
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label>Created At</label>
                    <input
                        type="datetime-local"
                        name="created_at"
                        value=""
                        // onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Video Link</label>
                    <input
                        type="url"
                        name="video_link"
                        value={formData.video_link}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <label>Status</label>
                    <select
                        name="status"
                        value=""
                        // onChange={handleInputChange}
                        required
                    >
                        <option value="draft">Draft</option>
                        <option value="published">Published</option>
                    </select>
                </div>
                <div className="form-group">
                    <label>Category</label>
                    <select
                        name="category"
                        value=""
                        // onChange={handleCategoryChange}
                        required
                    >
                        <option value="">Select a category</option>
                        <option value="">category1</option>
                        <option value="">category2</option>
                        {/* {categories.map(category => (
                            <option key={category.id} value={category.id}>{category.name}</option>
                        ))} */}
                    </select>
                </div>
                {/* {[...Array(8).keys()].map(i => (
                    <div className="form-group" key={i}>
                        <label>Image Field {i + 1}</label>
                        <select
                            name={`image_field_${i + 1}`}
                            value={formData[`image_field_${i + 1}`]}
                            onChange={handleImageChange}
                        >
                            <option value="">Select an image</option>
                            {images.map(image => (
                                <option key={image.id} value={image.id}>{image.title}</option>
                            ))}
                        </select>
                    </div>
                ))} */}
                {/* <button type="submit">{id ? 'Update Post' : 'Upload Post'}</button> */}
                <button type="submit">Upload Post</button>
            </form>
        </div>
    )
}

export default ContentHistoryCreate
