import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const progressive_bar = () => {
  // const { id } = useParams();

  const handleInputChange = (e) => {
    // const { name, value } = e.target;
    // setFormData({
    //   ...formData,
    //   [name]: value
    // });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  }

  return (
    <div className="form-container">
      {/* <h2>{id ? 'Update Progressive Bar' : 'Create Progressive Bar'}</h2> */}
      <h2>Create Progressive Bar</h2>
      <form onSubmit={handleSubmit}>
        {[...Array(6).keys()].map(i => (
          <div className="form-group-pair" key={`pair_${i}`}>
            <div className="form-group" key={`title_${i}`}>
              <label>Title_{i + 1}</label>
              <input
                type="text"
                name={`title_${i + 1}`}
                value=""
                onChange={handleInputChange}
                rows="10"
                required
              />
            </div>

            <div className="form-group" key={`amount_${i}`}>
              <label>Amount_{i + 1}</label>
              <input
                type="text"
                name={`amount_${i + 1}`}
                value=""
                onChange={handleInputChange}
                required
              />
            </div>
          </div>
        ))}

        {/* <button type="submit">{id ? 'Update Progressive Bar' : 'Create Progressive Bar'}</button> */}
        <button type="submit">Create Progressive Bar</button>
      </form>
    </div>
  )
}

export default progressive_bar
