import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const project_content = () => {
  const [formData, setFormData] = useState({
    title: '',
    sub_title: '',
    details: '',
    image: '',
    video_link: '',
    content_type: 'general',
    project_type: '',
    created_at: ''
  });

  const [project_type, setProjectType] = useState([]);
  const [images, setImages] = useState([]);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/project-type/views')
        .then(response => {
          setProjectType(response.data);
        })
        .catch(error => {
            console.error('Error fetching Project types:', error);
        });

    axios.get('http://127.0.0.1:8000/api/settings/images/')
        .then(response => {
            setImages(response.data);
        })
        .catch(error => {
            console.log('Error fetching images:', error);
        });
    if(id){
      axios.get(`http://127.0.0.1:8000/api/project-content/update/<int:pk>/${id}/`)
      .then(response => {
          const data = response.data;
          const formattedDate = data.created_at.slice(0, 16);
          setFormData({
              ...data,
              created_at: formattedDate,
              category: data.category || '',
              image: data.image || '',
              project_type: data.project_type || '',
     
          });
      })
      .catch(error => {
          console.error('Error fetching blog post:', error);
      });
    }

  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="form-container">
      {/* <h2>{id ? 'Update About Page' : 'Create About Page'}</h2> */}
      <h2>Create project content</h2>
      <form onSubmit={handleSubmit}>

        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            name="title"
            value=""
            // onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Sub-title</label>
          <input
            type="text"
            name="sub_title"
            value=""
            // onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Details</label>
          <textarea
            name="details"
            value=""
            // onChange={handleInputChange}
            rows="10"
            required
          ></textarea>
        </div>

        <div className="form-group">
          <label>Image</label>
          <select
            name="image"
            value=""
          // onChange={handleimageChange}
          >
            <option value="">Select an image</option>
            {/* {images.map(image => (
                <option key={image.id} value={image.id}>{image.title}</option>
              ))} */}
          </select>
        </div>

        <div className="form-group">
          <label>Video Link</label>
          <input
            type="url"
            name="video_link"
            value=""
          // onChange={handleInputChange}
          />
        </div>

        <div className="form-group">
          <label>Content Type</label>
          <select
            name="content_type"
            value=""
            // onChange={handleInputChange}
            required
          >
            <option value="approch">Approch</option>
            <option value="impact">Impact</option>
            <option value="general">General</option>
          </select>
        </div>

        <div className="form-group">
          <label>Project Type</label>
          <select
            name="project_type"
            value=""
            // onChange={handleCategoryChange}
            required
          >
            <option value="">Select Project Type</option>
            {/* {categories.map(category => (
              <option key={category.id} value={category.id}>{category.name}</option>
            ))} */}
          </select>
        </div>

        <div className="form-group">
          <label>Created At</label>
          <input
            type="datetime-local"
            name="created_at"
            value=""
            // onChange={handleInputChange}
            required
          />
        </div>

        {/* <button type="submit">{id ? 'Update About' : 'Create About'}</button> */}
        <button type="submit">Create project content</button>
      </form>
    </div>
  );
}

export default project_content
