import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './contenthistory.css';

const ContentHistoryCreate = () => {
    const [formData, setFormData] = useState({
        title: '',
        subtitle: '',
        details: '',
        video_link: '',
        created_date: '',
        history_type: 'general',
        image_field_1: '',
        image_field_2: '',
        image_field_3: '',
        image_field_4: ''
    });

    const [images, setImages] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {

        axios.get('http://127.0.0.1:8000/api/settings/images/')
            .then(response => {
                setImages(response.data);
            })
            .catch(error => {
                console.log('Error fetching images:', error);
            });

    });


    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleimageChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value || ''
        });
    };
      const handleSubmit = async (e) => {
        console.log("The data is going to submit", formData);
        // e.preventDefault();
        // try {
        //   const token = localStorage.getItem('token');
        //   const response = await axios.post('http://localhost:8000/api/your-endpoint/', formData, {
        //     headers: {
        //       Authorization: `Bearer ${token}`,
        //     },
        //   });
        //   console.log('Form submitted successfully:', response.data);
        // } catch (error) {
        //   console.error('Error submitting form:', error);
        // }
      };

    return (
        <div className="form-container">
            {/* <h2>{id ? 'Update Blog Post' : 'Upload Blog Post'}</h2> */}
            <h2>Create History Content</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Title</label>
                    <input
                        type="text"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Sub Title</label>
                    <input
                        type="text"
                        name="subtitle"
                        value={formData.subTitle}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Details</label>
                    <textarea
                        name="details"
                        value={formData.details}
                        onChange={handleInputChange}
                        rows="10"
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label>Video Link</label>
                    <input
                        type="url"
                        name="video_link"
                        value={formData.video_link}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <label>Created At</label>
                    <input
                        type="datetime-local"
                        name="created_date"
                        value={formData.created_at}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Type</label>
                    <select
                        name="history_type"
                        value={formData.history_type}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="general">General</option>
                        <option value="history">History</option>
                    </select>
                </div>
                {[...Array(4).keys()].map(i => (
                    <div className="form-group" key={i}>
                        <label>Image Field {i + 1}</label>
                        <select
                            name={`image_field_${i + 1}`}
                            value={formData[`image_field_${i + 1}`]}
                            onChange={handleimageChange}
                        >
                            <option value="">Select an image</option>
                            {images.map(image => (
                            <option key={image.id} value={image.id}>{image.title}</option>
                            ))}
                        </select>
                    </div>
                ))}
                {/* <button type="submit">{id ? 'Update Post' : 'Upload Post'}</button> */}
                <button type="submit">Upload Content</button>
            </form>
        </div>
    )
}

export default ContentHistoryCreate
