import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './ContentGallery.css';
import image1 from "../../assets/images/image1.jpg";
import image2 from "../../assets/images/image2.jpg";
import image3 from "../../assets/images/image3.jpg";

const ContentGallery = () => {

    const [allcontetsgallery, setAllcontentgallery] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:8000/api/historycontents/')
            .then(response => {
                setAllcontentgallery(response.data);
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>;

    return (
        <div className="content-list">
            <div className='header'>
                <h1>Content Gallery</h1>
                <Link to="/content-gallery/create" className="create-gallery-btn">Create Content Gallery</Link>
            </div>
            <div className="content-cards">
                {allcontetsgallery.map(allcontent => (
                    <div className="content-card" key={allcontent.id}>
                        <p><strong>Title:</strong> {allcontent.title} </p>
                        <div className='imges-list'>
                            <img src={allcontent.image_field_1 ? `http://127.0.0.1:8000${allcontent.image_field_1}` : image1} alt="image1" className="iamges" />
                            <img src={allcontent.image_field_2 ? `http://127.0.0.1:8000${allcontent.image_field_2}` : image2} alt="image2" className="iamges" />
                            <img src={allcontent.image_field_3 ? `http://127.0.0.1:8000${allcontent.image_field_3}` : image3} alt="image3" className="iamges" />
                        </div>
                        <Link to={`#`} className="update-link">Update</Link>
                    </div>
                ))}
            </div>

        </div>
    );
};

export default ContentGallery;
