// src/components/MessageDetail/MessageDetail.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './styles.css';

const MessageDetail = () => {
  const { id } = useParams();
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8000/api/admin/messages/${id}/reply/`)
      .then(response => {
        setMessage(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;
  if (!message) return <div>Message not found</div>;

  return (
    <div className="message-detail">
      <h1>Message Detail</h1>
      <p><strong>Name:</strong> {message.name}</p>
      <p><strong>Email:</strong> {message.email}</p>
      <p><strong>Subject:</strong> {message.subject}</p>
      <p><strong>Message:</strong> {message.message}</p>
      <p><strong>Created At:</strong> {message.created_at}</p>
      {message.admin_reply && (
        <>
          <p><strong>Admin Reply:</strong> {message.admin_reply}</p>
          <p><strong>Replied At:</strong> {message.replied_at}</p>
        </>
      )}
    </div>
  );
};

export default MessageDetail;
