import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './progress.css';


const progressive_bar_list_view = () => {
  const [progress, setProgress] = useState([]);
  const navigate = useNavigate();
  
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/progress/views')
      .then(response => {
        console.log("Progress Data", response.data)
        setProgress(response.data);
      })
      .catch(error => {
        console.error('Error fetching Progress Data:', error);
        alert("Error getting Progress Data")
      });
  }, []);

  return (
    <div className="progress-list">
      <div className="header">
        <h1>Who We Are</h1>
        <Link to="#" className="create-progress-btn">Create About Page</Link>
      </div>
      <hr className="separator-line" />
      <div className='Allcardlist'>
        {about.length === 0 ? (
          <div className="no-posts">No progress bar created yet!!</div>
        ) : (
          progress.map(post => (
            <div key={post.id} className="progress-card">
              <div className="progress-content">
                <h2>Progress Bar</h2>
                <p>Created Date: {new Date(post.created_date).toLocaleDateString()}</p>
                <p>Content: {truncateContent(post.history_content, 50)}</p>
                <div className="progress-actions">
                  <button className="btn-delete" onClick={() => handleDelete(post.id)}>Delete</button>
                  <button className="btn-update" onClick={() => handleUpdate(post.id)}>Update</button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default progressive_bar_list_view
