import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './UserProfile.css';
import suerphoto from '../../assets/demouser.png';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem('token');
      console.log('Token in UserProfile:', token);
      if (token) {
        try {
          const response = await axios.get('http://localhost:8000/api/current-user/', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          setUser(response.data);
        } catch (error) {
          console.error('Error fetching user data:', error);
          setError('Failed to load user data');
        }
      }
    };

    fetchUser();
  }, []);

  if (!user) {
    return <div className="user-profile"><p>{error}</p></div>;
  }

  return (
    <div className="user-profile">
      <img
        src={user.upload_image ? `http://localhost:8000${user.upload_image}` : suerphoto}
        alt="User"
        className="user-photo"
      />
      <div className="user-info">
        <p className="user-name">{user.first_name} {user.last_name}</p>
        <p className="user-role">{user.user_role}</p>
        <button className="signout-btn" onClick={handleSignOut}>SignOut</button>
      </div>
    </div>
  );
};

const handleSignOut = () => {
  localStorage.removeItem('token');
  window.location.href = '/login'; // Redirect to login page
};

export default UserProfile;
