//         role: 'Moderator',
//         image: 'user2.jpg'
//     },
//     {
//         id: 6,
//         name: 'Jane Smith',
//         email: 'jane.smith@example.com',
//         role: 'Moderator',
//         image: 'user2.jpg'
//     },
//     // Add more user objects as needed
// ];

const UserList = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
  
    useEffect(() => {
      axios.get('http://localhost:8000/api/users/')
        .then(response => {
          console.log("user data", response.data)
          setUsers(response.data);
          setLoading(false);
        })
        .catch(error => {
          setError(error);
          setLoading(false);
        });
    }, []);
  
    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>;

    return (
        <div className="container">
            <div className="header">
                <h1>Welcome to User Registration</h1>
            </div>
            <div className="register-button">
                <button onClick={() => window.location.href = '/users/create'}>Register new user</button>
            </div>
            <hr />
            {/* <div className="card-list">
                {users.map(user => (
                    <div className="card" key={user.id}>
                        <img src={demo} alt={user.name} />
                        <div className="card-info">
                            <h2>{user.name}</h2>
                            <p>Email: {user.email}</p>
                            <p>Role: {user.role}</p>
                        </div>
                    </div>
                ))}
            </div> */}
            <div className="users-container">
                {users.map(user => (
                <div key={user.id} className="user-card">
                     <img src={`http://127.0.0.1:8000${user.photo}`} alt={user.name} className="user-photo" />
                    <h2>{user.name}</h2>
                    <p><strong>Email:</strong> {user.email}</p>
                    <p><strong>Role:</strong> {user.role}</p>
                </div>
            ))}
            </div>
        </div>
    );
};

export default UserList;
