import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BlogPost.css';

const BlogPostForm = () => {
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        created_at: '',
        slug: '',
        status: 'draft',
        category: 0
    });

    const [categories, setCategories] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/api/category/view/')
            .then(response => {
                setCategories(response.data);
            })
            .catch(error => {
                console.error('Error fetching categories:', error);
            });
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleCategoryChange = (e) => {
        console.log("e.target.value ",e.target.value)
        const selectedCategoryName = e.target.value;
        const selectedCategory = categories.find(category => category.name === selectedCategoryName);
        const categoryId = selectedCategory ? selectedCategory.id : '';
        console.log("category id ",categoryId)

        setFormData({
            ...formData,
            category: categoryId
        });

        console.log("formData ",formData)

    };

    const handleBlogPostCreation = () => {
        console.log(formData);
        const postData = {
            ...formData,
            category: formData.category ? parseInt(formData.category) : null
        };

        console.log(postData);
        axios.post('http://127.0.0.1:8000/api/blog/create/', postData)
            .then(response => {
                console.log('Blog post created successfully:', response.data);
                alert("Success");
                setFormData({
                    title: '',
                    content: '',
                    created_at: '',
                    slug: '',
                    status: 'draft',
                    category: ''
                });
            })
            .catch(error => {
                console.error('Error creating blog post:', error);
                alert("Error creating blog post");
                console.error(error.response.data);
            });
    };

    return (
        <div className="container">
            <h1>Upload Blog Post</h1>
            <div>
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="content">Content</label>
                    <textarea
                        id="content"
                        name="content"
                        value={formData.content}
                        onChange={handleInputChange}
                        rows="10"
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="created_at">Created At</label>
                    <input
                        type="datetime-local"
                        id="created_at"
                        name="created_at"
                        value={formData.created_at}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="slug">Slug</label>
                    <input
                        type="text"
                        id="slug"
                        name="slug"
                        value={formData.slug}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="status">Status</label>
                    <select
                        id="status"
                        name="status"
                        value={formData.status}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="draft">Draft</option>
                        <option value="published">Published</option>
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <select
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleCategoryChange}
                        required
                    >
                        <option value="">Select a category</option>
                        {categories.map(category => (
                            <option key={category.id} value={category.id}>
                                {category.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <button onClick={handleBlogPostCreation} type="button">Upload Post</button>
                </div>
            </div>
        </div>
    );
};

export default BlogPostForm;
