import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './UserProfile.css';
import suerphoto from '../../assets/demouser.png';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    axios.get('http://127.0.0.1:8000/api/current-user/', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
    .then(response => {
      console.log('User data:', response.data);
      setUser(response.data);
    })
    .catch(error => {
      console.error('Error fetching user data:', error);
      if (error.response && error.response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      }
    });
  }, [navigate]);

  const handleSignOut = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="user-profile">
      <img
        src={user.upload_image ? `http://127.0.0.1:8000${user.upload_image}` : suerphoto}
        alt="User"
        className="user-photo"
      />
      <div className="user-info">
        <p className="user-name">{user.first_name} {user.last_name}</p>
        <p className="user-role">{user.user_role}</p>
        <button className="signout-btn" onClick={handleSignOut}>SignOut</button>
      </div>
    </div>
  );
};

export default UserProfile;
