import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './UserProfile.css';
import suerphoto from '../../assets/demouser.png';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://127.0.0.1:8000/api/current-user/', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setUser(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setError('Failed to load user data');
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) {
    return <div className="user-profile">Loading...</div>;
  }

  if (error) {
    return <div className="user-profile">{error}</div>;
  }

  return (
    <div className="user-profile">
      <img
        src={user && user.upload_image ? `http://127.0.0.1:8000${user.upload_image}` : suerphoto}
        alt="User"
        className="user-photo"
      />
      <div className="user-info">
        <p className="user-name">{user ? `${user.first_name} ${user.last_name}` : 'Jhone Doe'}</p>
        <p className="user-role">{user ? user.user_role : 'Developer'}</p>
        <button className="signout-btn" onClick={() => {
          localStorage.removeItem('token');
          window.location.href = '/login';
        }}>SignOut</button>
      </div>
    </div>
  );
};

export default UserProfile;
