import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SettingsStyle.css';

const Setting = () => {
    const [blogcategories, setBlogCategories] = useState([]);

    const[blogcategorydata, setBlogCategoryData] = useState({
        name : '',
    });

    useEffect(() => {

        axios.get('http://127.0.0.1:8000/api/category/view/')
            .then(response => {
                console.log("categories data", response.data);
                setBlogCategories(response.data);
            })
            .catch(error => {
                console.error('Error fetching about page:', error);
            });

    }, []);

    const hangleCategoryChanges = (e) => {
        setBlogCategoryData({
            ...blogcategorydata,
            name: e.target.value
        });
    }

    const handleCategoryData = (e)  =>{
        e.preventDefault();
        axios.post('http://127.0.0.1:8000/api/category/create/', blogcategorydata)
        .then(response => {
          alert("Category created successfully");
        })
        .catch(error => {
          console.error('Error creating Category:', error);
          alert("Error creating Category");
        });
    }

    return (
        <div className="setting-page">
            <div className="setting-header">
                <h1>Setting</h1>
            </div>
            <div className='setting-body'>
                <div className='Blog-category'>
                    <div className='Blog-category-input'>
                        <label>Insert Category name</label>
                        <input
                            type="text"
                            name="title"
                            value={blogcategorydata.name}
                            onChange={hangleCategoryChanges}
                        />
                        <button type="submit" onClick={handleCategoryData} className='btn-create'>Create Category</button>
                    </div>
                    <div className='Blog-category-view'>
                        <h5>All Categories</h5>
                        <div className='Blog-category-list'>
                            {blogcategories.length === 0 ? (
                                <div className="no-posts">No About Page created yet!!</div>
                            ):(blogcategories.map(categories => (
                                <p>{categories.name}</p>

                            ))
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Setting;
