import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

const Dashboard = () => {
  const [logs, setLogs] = useState([]); // State to store log data

  useEffect(() => {
    // Fetch logs from the API
    axios.get('http://127.0.0.1:8000/api/logs/views/')
      .then(response => {
        console.log("Get Data blog", response.data);
        setLogs(response.data); // Store response data in state
      })
      .catch(error => {
        console.error('Error fetching Data:', error);
      });
  }, []);

  return (
    <div className="dashboard">
      <div className='LogList'>
        <div className='LogList-nav'>
          <h3>Recent Actions</h3>
        </div>
        <div className='LogList-body'>
          <ul>
            {logs.map(log => (
              <li key={log.id}>
                {/* Format: [Time] - [User] - [Action] */}
                <span className="log-time">{new Date(log.action_time).toLocaleString()}</span> - 
                <span className="log-user">{log.user_details}</span> - 
                <span className="log-action">
                  {log.action === 'Change' ? 'Changed' : 
                  log.action === 'Addition' ? 'Added' : 
                  'Deleted'} {log.object_repr} 
                </span>
                <span className="log-message">- {log.change_message}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
