import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './sidebar.css';
import logo from '../../assets/images/logo.png';

const Sidebar = () => {
  const [submenuOpen, setSubmenuOpen] = useState(false);

  const toggleSubmenu = () => {
    setSubmenuOpen(!submenuOpen);
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      <nav className="sidebar-nav">
        <ul>
          <li className="active"><i className="fas fa-home"></i> Dashboard</li>
          <li><i className="fas fa-hand-holding-heart"></i> Blog</li>
          <li onClick={toggleSubmenu} className={submenuOpen ? "submenu-open" : ""}>
            <i className="fas fa-users"></i> Contract Messages
            {submenuOpen && (
              <ul className="submenu">
                <li>
                  <Link to="/all-messages">All messages</Link>
                </li>
              </ul>
            )}
          </li>
          <li><i className="fas fa-cog"></i> Settings</li>
        </ul>
      </nav>
    </div>
  );
};

export default Sidebar;
