import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BlogPost.css';

const BlogPostForm = () => {
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        created_at: '',
        slug: '',
        status: 'draft',
        category: '',
        image_field_1: '',
        image_field_2: '',
        image_field_3: '',
        image_field_4: '',
        image_field_5: '',
        image_field_6: '',
        image_field_7: '',
        image_field_8: ''
    });

    const [categories, setCategories] = useState([]);
    const [images, setImages] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/api/category/view/')
            .then(response => {
                setCategories(response.data);
            })
            .catch(error => {
                console.error('Error fetching categories:', error);
            });

        axios.get('http://127.0.0.1:8000/api/settings/images/')
             .then(response => {
                setImages(response.data);
             })
             .catch(
                error => {
                    console.log('Error for fetching images data', error);
                }
             );
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleCategoryChange = (e) => {
        setFormData({
            ...formData,
            category: e.target.value
        });
    };

    const handleImageChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleBlogPostCreation = () => {
        console.log("before sending the blogpost data ", formData);
        const postData = {
            ...formData,
            category: formData.category ? parseInt(formData.category) : null
        };

        console.log("The sending blogpost data is ", postData);
        axios.post('http://127.0.0.1:8000/api/blog/create/', postData)
            .then(response => {
                console.log('Blog post created successfully:', response.data);
                alert("Success");
                setFormData({
                    title: '',
                    content: '',
                    created_at: '',
                    slug: '',
                    status: 'draft',
                    category: ''
                });
            })
            .catch(error => {
                console.error('Error creating blog post:', error);
                alert("Error creating blog post");
                console.error(error.response.data);
            });
    };

    return (
        <div className="container">
            <h1>Upload Blog Post</h1>
            <div>
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="content">Content</label>
                    <textarea
                        id="content"
                        name="content"
                        value={formData.content}
                        onChange={handleInputChange}
                        rows="10"
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="created_at">Created At</label>
                    <input
                        type="datetime-local"
                        id="created_at"
                        name="created_at"
                        value={formData.created_at}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="slug">Slug</label>
                    <input
                        type="text"
                        id="slug"
                        name="slug"
                        value={formData.slug}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="status">Status</label>
                    <select
                        id="status"
                        name="status"
                        value={formData.status}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="draft">Draft</option>
                        <option value="published">Published</option>
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <select
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleCategoryChange}
                        required
                    >
                        <option value="">Select a category</option>
                        {categories.map(category => (
                            <option key={category.id} value={category.id}> {category.name} </option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <button onClick={handleBlogPostCreation} type="button">Upload Post</button>
                </div>
            </div>
        </div>
    );
};

export default BlogPostForm;
