import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './UserCreateForm.css';

const RegisterUser = () => {
    const { register, handleSubmit, formState: { errors }, reset } = useForm();
    const navigate = useNavigate();
    const { id } = useParams();
    const [isEditMode, setIsEditMode] = useState(false);

    useEffect(() => {
        if (id) {
            setIsEditMode(true);
            axios.get(`http://127.0.0.1:8000/api/users/${id}/`)
                .then(response => {
                    reset(response.data); // Reset form with the fetched data
                })
                .catch(error => {
                    console.error('Error fetching user data: ', error);
                });
        }
    }, [id, reset]);

    const onSubmit = data => {
        const formData = new FormData();
    
        for (let key in data) {
            // if (key === 'upload_image') {
            //     if (data[key] && data[key].length > 0) {
            //         formData.append(key, data[key][0]);
            //     }
            // }
            if (key === 'upload_image' && data[key].length > 0) {
                formData.append(key, data[key][0]);  // Ensure the file is correctly appended
            }            
            else if (key === 'password') {
                if (data[key]) {
                    formData.append(key, data[key]);  // Only add password if provided
                }
            } else {
                formData.append(key, data[key]);
            }
        }
    
        const url = isEditMode 
            ? `http://localhost:8000/api/users/${id}/`
            : 'http://localhost:8000/api/users/create';
    
        const method = isEditMode ? 'put' : 'post';
        
        console.log('Form Data:', [...formData.entries()]);
        
        axios({
            method: method,
            url: url,
            data: formData,
            headers: {
                'Content-Type': 'multipart/form-data',
            }
        })
        .then(response => {
            alert("Success");
            reset();
            navigate(-1);
        })
        .catch(error => {
            console.error('Error saving user:', error);
            alert("Error saving user");
            console.error(error.response.data);
        });
    };
    
    

    return (
        <div className="form-container">
            <h2>{isEditMode ? 'Edit User Account' : 'Create User Account'}</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div className="form-group">
                    <label>First Name</label>
                    <input {...register('first_name', { required: true })} />
                    {errors.first_name && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Last Name</label>
                    <input {...register('last_name', { required: true })} />
                    {errors.last_name && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Username</label>
                    <input {...register('username', { required: true })} />
                    {errors.username && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Email</label>
                    <input type="email" {...register('email', { required: true })} />
                    {errors.email && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" {...register('password', { minLength: 8 })} />
                    {errors.password && <span>Password must be at least 8 characters long</span>}
                </div>

                <div className="form-group">
                    <label>User Role</label>
                    <select {...register('user_role', { required: true })}>
                        <option value="superuser">SuperUser</option>
                        <option value="moderator">Moderator</option>
                        <option value="management">Management</option>
                    </select>
                    {errors.user_role && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Account Status</label>
                    <select {...register('account_status', { required: true })}>
                        <option value="active">Active</option>
                        <option value="inactive">Inactive</option>
                    </select>
                    {errors.account_status && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Upload Image</label>
                    <input type="file" {...register('upload_image')} />
                </div>

                <div className="form-group">
                    <label>Is Staff</label>
                    <input type="checkbox" {...register('is_staff')} />
                </div>

                <div className="form-group">
                    <label>Is Active</label>
                    <input type="checkbox" {...register('is_active')} />
                </div>

                <button type="submit">{isEditMode ? 'Update User' : 'Create User'}</button>
            </form>
        </div>
    );
};

export default RegisterUser;
