import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './styles.css';

const MessageDetail = () => {
  const { id } = useParams();
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [adminReply, setAdminReply] = useState('');
  const [forwardedTo, setForwardedTo] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8000/api/admin/messages/${id}/replay/`)
      .then(response => {
        setMessage(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
      });
  }, [id]);

  const handleReplyChange = (event) => {
    setAdminReply(event.target.value);
  };

  const handleForwardChange = (event) => {
    const selectedUsers = Array.from(event.target.selectedOptions, option => option.value);
    setForwardedTo(selectedUsers);
  };

  const handleSendReply = () => {
    axios.post(`http://localhost:8000/api/admin/messages/${id}/replay/`, {
      admin_reply: adminReply,
      forwarded_to: forwardedTo
    })
      .then(response => {
        setMessage(response.data);
        setAdminReply('');
        setForwardedTo([]);
        alert('Reply sent successfully!');
      })
      .catch(error => {
        alert('Failed to send reply. Please try again.');
      });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;
  if (!message) return <div>Message not found</div>;

  return (
    <div className="container">
      <h1>Message Detail</h1>
      <div className="detail"><strong>Name:</strong> {message.name}</div>
      <div className="detail"><strong>Email:</strong> {message.email}</div>
      <div className="detail"><strong>Subject:</strong> {message.subject}</div>
      <div className="message">
        <strong>Message:</strong>
        <p>{message.message}</p>
      </div>
      <div className="detail"><strong>Created At:</strong> {message.created_at}</div>
      {message.admin_reply && (
        <>
          <div className="detail"><strong>Admin Reply:</strong> {message.admin_reply}</div>
          <div className="detail"><strong>Replied At:</strong> {message.replied_at}</div>
        </>
      )}
      <div className="reply-box">
        <textarea
          value={adminReply}
          onChange={handleReplyChange}
          placeholder="Type your reply here..."
        />
        <select multiple onChange={handleForwardChange} value={forwardedTo}>
          {/* Populate options with organization members */}
          {message.forwarded_to.map(user => (
            <option key={user.id} value={user.id}>{user.username}</option>
          ))}
        </select>
        <button onClick={handleSendReply}>Send Reply</button>
      </div>
    </div>
  );
};

export default MessageDetail;
