import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './BlogPost.css';

const BlogPostList = () => {
    const [blogPosts, setBlogPosts] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/api/blog/view/')
            .then(response => {
                setBlogPosts(response.data);
            })
            .catch(error => {
                console.error('Error fetching blog posts:', error);
            });
    }, []);

    const truncateContent = (content, maxLength) => {
        if (content.length <= maxLength) {
            return content;
        }
        return content.substring(0, maxLength) + '...';
    };

    return (
        <div className="blog-post-list">
            <div className="header">
                <h1>Welcome to Blog Post</h1>
                <Link to="/blog/create" className="create-blog-post-btn">Create Blog Post</Link>
            </div>
            {blogPosts.map(post => (
                <div key={post.id} className="blog-post-card">
                    <img src={post.image_field_1} alt={post.title} className="blog-post-image" />
                    <div className="blog-post-content">
                        <h2>Title: {post.title}</h2>
                        <p>Date: {new Date(post.created_at).toLocaleDateString()}</p>
                          <p>Content: {truncateContent(post.content, 20)}</p>
                        <div className="blog-post-actions">
                            <button className="delete-btn">Delete</button>
                            <button className="update-btn">Update</button>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default BlogPostList;
