import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const About = () => {
  const [about, setAbout] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/abouts/list/')
      .then(response => {
        console.log("Get Data about", response.data)
        setAbout(response.data);
      })
      .catch(error => {
        console.error('Error fetching about page:', error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/abouts/delete/${id}/`)
      .then(response => {
        setAbout(about.filter(aboutpage => aboutpage.id !== id));
        alert("Deleted Succfully");
      })
      .catch(error => {
        alert("Error deleting About");
        console.error('Error deleting About:', error);
      });
  };

  const handleUpdate = (id) => {
    navigate(`/about/update/${id}`);
  };

  const truncateContent = (content, maxLength) => {
    if (content.length <= maxLength) {
      return content;
    }
    return content.substring(0, maxLength) + '...';
  };


  return (
    <div className="blog-post-list">
      <div className="header">
        <h1>What we do</h1>
        <Link to="/about/create" className="create-blog-post-btn">Create About Page</Link>
      </div>
      <hr className="separator-line" />
      {about.length === 0 ? (
        <div className="no-posts">No Page available</div>
      ) : (
        blogPosts.map(post => (
          <div key={post.id} className="blog-post-card">
            {post.image_field_1 && (
              <img src={`http://127.0.0.1:8000${post.image_field_1.image}`} alt={post.image_field_1.title} className="blog-post-image" />
            )}
            <div className="blog-post-content">
              <h2>Title: {post.title}</h2>
              <p>Date: {new Date(post.created_at).toLocaleDateString()}</p>
              <p>Content: {truncateContent(post.content, 20)}</p>
              <div className="blog-post-actions">
                <button className="delete-btn" onClick={() => handleDelete(post.id)}>Delete</button>
                <button className="update-btn" onClick={() => handleUpdate(post.id)}>Update</button>
              </div>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default About;
