import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const progressive_bar = () => {
  const { id } = useParams();

  const handleInputChange = (e) => {
    // const { name, value } = e.target;
    // setFormData({
    //   ...formData,
    //   [name]: value
    // });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  }

  return (
    <div className="form-container">
      <h2>{id ? 'Update Progressive Bar' : 'Create Progressive Bar'}</h2>
      <form onSubmit={handleSubmit}>

        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            name="title_1"
            value=""
            onChange={handleInputChange}
            rows="10"
            required
          ></input>
        </div>

        <div className="form-group">
          <label>Amount</label>
          <input
            type="text"
            name="amount_1"
            value=""
            // value={formData.created_date}
            onChange={handleInputChange}
            required
          />
        </div>

        <button type="submit">{id ? 'Update Progressive Bar' : 'Create Progressive Bar'}</button>
      </form>
    </div>
  )
}

export default progressive_bar
