import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './UserCreateForm.css';

const RegisterUser = () => {
    const { register, handleSubmit, formState: { errors }, reset } = useForm();
    const navigate = useNavigate();
    const { id } = useParams();
    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        username: '',
        email: '',
        user_role: '',
        account_status: 'inactive',
        upload_image: '',
        is_staff: false,
        is_active: false
    });

    useEffect(() => {        
        if (id) {
                axios.get(`http://127.0.0.1:8000/api/users/${id}/`)
                    .then(response => {
                        const data = response.data;
                        setFormData({
                            ...data,
                            image_field_1: data.image_field_1 || '',
                            image_field_2: data.image_field_2 || '',
                            image_field_3: data.image_field_3 || '',
                            image_field_4: data.image_field_4 || '',
                            image_field_5: data.image_field_5 || '',
                            image_field_6: data.image_field_6 || '',
                            image_field_7: data.image_field_7 || '',
                            image_field_8: data.image_field_8 || '',
                            image_field_9: data.image_field_9 || '',
                            image_field_10: data.image_field_10 || '',
                            image_field_11: data.image_field_11 || '',
                            image_field_12: data.image_field_12 || ''
                        });
                    })
                    .catch(error => {
                        console.error('Error fetching blog post:', error);
                    });
        }
    
    }, [id]);

    const onSubmit = data => {
        const formData = new FormData();
        for (let key in data) {
            if (key === 'upload_image') {
                formData.append(key, data[key][0]);
            } else {
                formData.append(key, data[key]);
            }
        }

        axios.post('http://localhost:8000/api/users/create', formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            }
        })
        .then(response => {
            alert("Success");
            reset();  
            navigate(-1);
        })
        .catch(error => {
            console.error('Error creating user:', error);
            alert("Error creating user");
            console.error(error.response.data);
        });
    };

    return (
        <div className="form-container">
            <h2>Create User Account</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div className="form-group">
                    <label>First Name</label>
                    <input {...register('first_name', { required: true })} />
                    {errors.first_name && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Last Name</label>
                    <input {...register('last_name', { required: true })} />
                    {errors.last_name && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Username</label>
                    <input {...register('username', { required: true })} />
                    {errors.username && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Email</label>
                    <input type="email" {...register('email', { required: true })} />
                    {errors.email && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" {...register('password', { required: true, minLength: 8 })} />
                    {errors.password && <span>This field is required and must be at least 8 characters long</span>}
                </div>

                <div className="form-group">
                    <label>User Role</label>
                    <select {...register('user_role', { required: true })}>
                        <option value="superuser">SuperUser</option>
                        <option value="moderator">Moderator</option>
                        <option value="management">Management</option>
                    </select>
                    {errors.user_role && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Account Status</label>
                    <select {...register('account_status', { required: true })}>
                        <option value="active">Active</option>
                        <option value="inactive">Inactive</option>
                    </select>
                    {errors.account_status && <span>This field is required</span>}
                </div>

                <div className="form-group">
                    <label>Upload Image</label>
                    <input type="file" {...register('upload_image')} />
                </div>

                <div className="form-group">
                    <label>Is Staff</label>
                    <input type="checkbox" {...register('is_staff')} />
                </div>

                <div className="form-group">
                    <label>Is Active</label>
                    <input type="checkbox" {...register('is_active')} />
                </div>

                <button type="submit">Create User</button>
            </form>
        </div>
    );
};

export default RegisterUser;
