import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './contenthistory.css';

const ContentList = () => {
    const [allcontents, setAllcontent] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:8000/api/historycontents/')
            .then(response => {
                setAllcontent(response.data);
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>;

    return (
        <div className="content-list">
            <div className='header'>
                <h1>Content History</h1>
                <Link to="/content-history/create" className="create-content-btn">Create History</Link>
            </div>
            <div className="content-cards">
                <div className="content-card">
                    <p><strong>Title:</strong> History 1 </p>
                    <p><strong>Created At :</strong> 27 June 2024</p>
                    <p><strong>Message:</strong> This content was create at july 27 of year 2024...</p>
                    {/* <Link to={`/message/${message.id}`} className="details-link">View Details</Link> */}
                </div>
                {allcontent.map(message => (
                    <div className="message-card" key={message.id}>
                        <p><strong>Name:</strong> {message.name}</p>
                        <p><strong>Email:</strong> {message.email}</p>
                        <p><strong>Message:</strong> {message.message.substring(0, 50)}...</p>
                        <Link to={`/message/${message.id}`} className="details-link">View Details</Link>
                    </div>
                ))}
            </div>

        </div>
    );
};

export default ContentList;
