import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './progress.css';


const progressive_bar_list_view = () => {
  const [progress, setProgress] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/progress/views')
      .then(response => {
        console.log("Progress Data", response.data)
        setProgress(response.data);
      })
      .catch(error => {
        console.error('Error fetching Progress Data:', error);
        alert("Error getting Progress Data")
      });
  }, []);

  return (
    <div className="content-list">
      <div className='header'>
        <h1>Progressive Bar</h1>
        <Link to="#" className="create-content-btn">Create ProgressBar</Link>
      </div>
      <hr className="separator-line" />
      <div className="content-cards">
        {allcontents.map(allcontent => (
          <div className="content-card" key={allcontent.id}>
            <p><strong>Title:</strong> {allcontent.title}</p>
            <p><strong>Created At:</strong> {allcontent.created_date}</p>
            <p><strong>Details:</strong> {allcontent.details.substring(0, 50)}...</p>
            <Link to={`/content-history/update/${allcontent.id}`} className="update-link">Update</Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default progressive_bar_list_view
