import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const project_list_view = () => {
  const [projects, setProject] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/project/views/')
      .then(response => {
        console.log("Get Data about", response.data)
        setProject(response.data);
      })
      .catch(error => {
        console.error('Error fetching project page:', error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/project/delete/${id}/`)
      .then(response => {
        setAbout(projects.filter(projectspage => projectspage.id !== id));
        alert("Deleted Succfully");
      })
      .catch(error => {
        alert("Error deleting Project");
        console.error('Error deleting Project:', error);
      });
  };

  const handleUpdate = (id) => {
    navigate(`/about/update/${id}`);
  };

  const truncateContent = (content, maxLength) => {
    if (content.length <= maxLength) {
      return content;
    }
    return content.substring(0, maxLength) + '...';
  };

  return (
    <div>
      <h5>All list view</h5>
    </div>
  )
}

export default project_list_view
