import React, { useState } from 'react';
import axios from 'axios';
import './BlogPost.css';

const BlogPostForm = () => {
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        created_at: '',
        slug: '',
        status: 'draft',
        tags: '',
        category: ''
    });

    const handleInputChange = (e) => {
        const value = e.target.name === 'tags' ? e.target.value.split(',') : e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value
        });
    };

    const handleblogpostcreation = () => {
        const formattedData = {
            ...formData,
            tags: formData.tags.split(',').map(tag => ({ name: tag.trim() })),
            category: { name: formData.category }
        };
    
        axios.post('http://127.0.0.1:8000/api/blog/create/', formattedData)
            .then(response => {
                console.log('Blog post created successfully:', response.data);
                alert("Success")
                setFormData({
                    title: '',
                    content: '',
                    created_at: '',
                    slug: '',
                    status: 'draft',
                    tags: '',
                    category: ''
                });
            })
            .catch(error => {
                console.error('Error creating blog post:', error);
                alert("Error creating blog post");
            });
    };

    return (
        <div className="container">
            <h1>Upload Blog Post</h1>
            <div>
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="content">Content</label>
                    <textarea
                        id="content"
                        name="content"
                        value={formData.content}
                        onChange={handleInputChange}
                        rows="10"
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="created_at">Created At</label>
                    <input
                        type="datetime-local"
                        id="created_at"
                        name="created_at"
                        value={formData.created_at}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="slug">Slug</label>
                    <input
                        type="text"
                        id="slug"
                        name="slug"
                        value={formData.slug}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="status">Status</label>
                    <select
                        id="status"
                        name="status"
                        value={formData.status}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="draft">Draft</option>
                        <option value="published">Published</option>
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="tags">Tags</label>
                    <input
                        type="text"
                        id="tags"
                        name="tags"
                        value={formData.tags}
                        onChange={handleInputChange}
                        placeholder="Comma-separated"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <input
                        type="text"
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <button onClick={handleblogpostcreation} type="button">Upload Post</button>
                </div>
            </div>
        </div>
    );
};

export default BlogPostForm;
