import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ImageLibrary.css';


const ImageLibrary = () => {
  const [images, setImages] = useState([]);
  const [title, setTitle] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [selectedImageId, setSelectedImageId] = useState(null);


  const apiEndpoint = 'http://127.0.0.1:8000/api/settings/images/';


  useEffect(() => {
    axios.get(apiEndpoint)
      .then(response => {
        setImages(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the images!", error);
      });
  }, []);


  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };


  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]);
  };


  const handleUpload = () => {
    if (title && imageFile) {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('image', imageFile);


      axios.post(apiEndpoint, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      })
        .then(response => {
          setImages([...images, response.data]);
          setTitle('');
          setImageFile(null);
        })
        .catch(error => {
          console.error("There was an error uploading the image!", error);
        });
    } else {
      alert('Please provide both title and image.');
    }
  };


  const handleUpdate = () => {
    console.log('selectedImageId title and imageFile',selectedImageId, title, imageFile);
    if (selectedImageId && title && imageFile) {
      const formData = new FormData();

      formData.append('title', title);
      formData.append('image', imageFile.title);

      console.log('Updating image with:', {
        selectedImageId,
        title,
        imageFile: imageFile.name
      });

      axios.put(`${apiEndpoint}${selectedImageId}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      })
      .then(response => {
        setImages(images.map(image => image.id === selectedImageId ? response.data : image));
        setTitle('');
        setImageFile(null);
        setSelectedImageId(null);
      })
      .catch(error => {
        console.error("There was an error updating the image!", error);
      });
  } else {
    alert('Please select an image to update and provide both title and image.');
  }
  };


  const handleImageClick = (image) => {
    console.log('image title, file and id', image.title, image.image, image.id)
    setTitle(image.title);
    setImageFile(image.image);
    setSelectedImageId(image.id);
  };


  return (
    <div className="image-library">
      <h1>Welcome to ImageLibrary</h1>
      <div className="upload-section">
        <div className='image-title-file'>
          <input
            type="text"
            placeholder="Image Title"
            value={title}
            onChange={handleTitleChange}
          />
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>
       
        <div className='button-class'>
          <button onClick={handleUpload}>Upload Image</button>
          <button onClick={handleUpdate} disabled={!selectedImageId}>Update Image</button>
        </div>
      </div>
      <hr className="divider"></hr>
      <div className="gallery-section">
        {images.length > 0 ? (
          <ul>
            {images.map((image) => (
              <li key={image.id} onClick={() => handleImageClick(image)}>
                <img src={`http://127.0.0.1:8000${image.image}`} alt={image.title} />
                <p>{image.title}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No images uploaded yet.</p>
        )}
      </div>
    </div>
  );
};


export default ImageLibrary;
